mapper.update:
UPDATE admin
SET apptype = #{apptype},
    code    = #{code}
WHERE id = #{id};

UPDATE xlist
SET showtype = #{showtype},
    txt1 = #{txt1},
    txt2 = #{txt2}
WHERE id = #{id};

mapper.select:
SELECT x.* FROM xlist x
                    INNER JOIN (
    SELECT id FROM xlist
    ORDER BY id DESC

        LIMIT #{pageSize} OFFSET #{pageStart}

) AS tmp ON x.id = tmp.id
ORDER BY x.id ASC ;

SELECT x.* FROM ylist x
                    INNER JOIN (
    SELECT id FROM ylist
    ORDER BY id DESC

        LIMIT #{pageSize} OFFSET #{pageStart}

) AS tmp ON x.id = tmp.id
ORDER BY x.id ASC;

SELECT x.* FROM admin x
                    INNER JOIN (
    SELECT id FROM admin
    ORDER BY id DESC

        LIMIT #{pageSize} OFFSET #{pageStart}

) AS tmp ON x.id = tmp.id
ORDER BY x.id ASC;
